import { createContext, useEffect, useState } from "react";
import axios from "axios";

// Creating a context API
export const AuthContext = createContext();

// To use the context we need this provider, as we need to use dark mode and toggle function in our other components
export const AuthContextProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(
            // check in-case we have visited this page before, to preserve dark mode state, if we have not false is returned thus light mode is used
    JSON.parse(localStorage.getItem("user")) //JSON parse changes string 'Auth' to boolean
  );

  const login = async (inputs) => {
    const res = await axios.post("http://localhost:8800/api/auth/login", inputs, {
      withCredentials: true, // needed to allow functionality with cookies
    });

    setCurrentUser(res.data);
  };

  // converts user data into a JSON string and stores within the users local storage
  useEffect(() => {
    localStorage.setItem("user", JSON.stringify(currentUser));
  }, [currentUser]);

  return (
    <AuthContext.Provider value={{ currentUser, login }}>
      {children}
    </AuthContext.Provider>
  );
};
