import "./navbar.scss"
import HomeIcon from '@mui/icons-material/Home';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import SearchOutlinedIcon from "@mui/icons-material/SearchOutlined";
import { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext";
import axios from "axios";


function Navbar() {
  const { currentUser } = useContext(AuthContext);
  const [search, setSearch] = useState(null);
  const [searchAllUsers, setSearchAllUsers] = useState([]);


  const handleChange = async e => {
    setSearch(e.target.value)
    const res = await axios.get("http://localhost:3001/api/users/find/allUsers", {
      withCredentials: true, // needed for working with cookies
    });
    setSearchAllUsers(res.data);
  };

  const handleClick = null;

  const navigate = useNavigate();





  return (
    <div className="navbar">
      <div className="left">
        <h1>trustSocial</h1>
      </div>
      <div className="middle">
        <div className="icons">
          <Link to="/">
            <HomeIcon
              className="button"
              fontSize="large"
            />
          </Link>
          <ChatBubbleOutlineIcon
            className="button"
            fontSize="large"
          />
          <Link to={`profile/${currentUser.id}`}>
            <AccountCircleIcon
              className="button"
              fontSize="large"
            />
          </Link>
        </div>
      </div>
      <div className="right">
        <div className="searchResults">
          <div className="search">
            <SearchOutlinedIcon className="searchButton" />
            <input type="text"
              placeholder="Search trustMe..."
              name="search"
              onChange={handleChange}
            />
          </div>
          {search !== null && search !== "" &&
            Object.keys(searchAllUsers).map((item, i) => (
              <div key={i}>
                {
                  search.includes(searchAllUsers[item].name) &&
                  <span onClick={() => navigate(`/profile/${searchAllUsers[item].id}`)}>
                    {<img src={searchAllUsers[item].profilePic} />}
                    {searchAllUsers[item].name}
                  </span>
                }
              </div>
            ))
          }
        </div>
      </div>
    </div >
  )
}

export default Navbar
