import { useState } from "react";
import "./update.scss";
import { makeRequest } from "../../axios";
import { useMutation, useQueryClient } from "@tanstack/react-query";


const Update = ({ setOpenUpdate, user }) => {


  const [profile, setProfile] = useState(null)
  const [texts, setTexts] = useState({
    name: "",
    city: "",
    website: "",
  });

  const handleChange = (e) => {
    setTexts((prev) => ({ ...prev, [e.target.name]: [e.target.value] }))
  };

  const queryClient = useQueryClient();

  const mutation = useMutation(
    (user) => {
      return makeRequest.put("/users", user);
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(["user"])
      },
    }
  );

  const handleClick = async e => {
    e.preventDefault();
    let profileUrl; // Preserves users current profile pic

    // if we have a file we will update the users profile with it with the new url from upload, otherwise we will use the previous pic
    profileUrl = profile ? await upload(profile) : user.profilePic


    mutation.mutate({ ...texts, profilePic: profileUrl });
    setOpenUpdate(false);
  };

  const upload = async (file) => {
    try {
      const formData = new FormData(); // we cannot send file directly to api so we must use formdata
      formData.append("file", file)
      const res = await makeRequest.post("/upload", formData);
      return res.data
    } catch (err) {
      console.log(err);
    }
  }



  return (
    <div className="update">
      Update
      <form>
        <input type="file" onChange={e => setProfile(e.target.files[0])} />
        <input type="text" name="name" placeholder="Name" onChange={handleChange} />
        <input type="text" name="city" placeholder="City" onChange={handleChange} />
        <input type="text" name="website" placeholder="Website" onChange={handleChange} />
        <button onClick={handleClick}>Update</button>
      </form>
      <button onClick={() => setOpenUpdate(false)}>X</button>
    </div>
  )
}

export default Update;
