import "./post.scss";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import FavoriteOutlinedIcon from "@mui/icons-material/FavoriteOutlined";
import TextsmsOutlinedIcon from "@mui/icons-material/TextsmsOutlined";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import MoodIcon from '@mui/icons-material/Mood';
import MoodBadIcon from '@mui/icons-material/MoodBad';
import { Link } from "react-router-dom";
import Comments from "../comments/Comments";
import React, { useState } from "react";
import { useQuery, useQueryClient, useMutation } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import { useContext } from "react";
import { AuthContext } from "../../contexts/authContext";
import { motion, AnimatePresence } from "framer-motion"
import Spinner from 'react-bootstrap/Spinner';

const Post = ({ post }) => {
  const [commentOpen, setCommentOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);
  const { currentUser } = useContext(AuthContext);


  // Extracting like data from API for a specific post
  const likes = useQuery(["likes", post.id], () =>
    makeRequest.get("/likes?postId=" + post.id).then((res) => {
      return res.data;
    })
  );

  // Handling positive trusts
  const positiveTrusts = useQuery(["positiveTrusts", post.id], () =>
    makeRequest.get("/positiveTrusts?postId=" + post.id).then((res) => {
      return res.data;
    })
  );

  // Handling negative trusts
  const negativeTrusts = useQuery(["negativeTrusts", post.id], () =>
    makeRequest.get("/negativeTrusts?postId=" + post.id).then((res) => {
      return res.data;
    })
  );

  const queryClient = useQueryClient();

  const mutation = useMutation(
    (liked) => {
      if (liked) return makeRequest.delete("/likes?postId=" + post.id);
      return makeRequest.post("/likes", { postId: post.id });
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["likes"]);
      },
    }
  );

  const ptMutation = useMutation(
    (trusted) => {
      if (trusted) return makeRequest.delete("/positiveTrusts?postId=" + post.id);
      return makeRequest.post("/positiveTrusts", { postId: post.id });
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["positiveTrusts"]);
      },
    }
  );

  const ntMutation = useMutation(
    (notTrusted) => {
      if (notTrusted) return makeRequest.delete("/negativeTrusts?postId=" + post.id);
      return makeRequest.post("/negativeTrusts", { postId: post.id });
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["negativeTrusts"]);
      },
    }
  );

  const handleLike = () => {
    mutation.mutate(likes.data.includes(currentUser.id))
  }

  const handlePositiveTrust = () => {
    ptMutation.mutate(positiveTrusts.data.includes(currentUser.id))
  }

  const handleNegativeTrust = () => {
    ntMutation.mutate(negativeTrusts.data.includes(currentUser.id))
  }

  // Deletes posts
  const deleteMutation = useMutation(
    (postId) => {
      return makeRequest.delete("/posts/" + postId);
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["posts"]);
      },
    }
  );

  const handleDelete = () => {
    deleteMutation.mutate(post.id);
  }


  // want a percentage in the case oh high amount of interaction, to improve understanding, provides 
  // most visibiliy to show both total interactions and percentage
  const positiveTrustTotal = positiveTrusts.data?.length;
  const negativeTrustTotal = negativeTrusts.data?.length;
  const totalRatings = positiveTrusts.data?.length + negativeTrusts.data?.length;
  let percentageTrust = null;
  if (negativeTrustTotal === 0 && positiveTrustTotal === 0) {
    percentageTrust = "No ratings";
  } else if (negativeTrustTotal === 0) {
    percentageTrust = 100;
  } else {
    percentageTrust = ((positiveTrustTotal / totalRatings) * 100).toPrecision(2);
  }

  // ANIMATION

  const Component = React.forwardRef((props, ref) => (
    <div ref={ref}></div>
  ))
  const MotionComment = motion(Comments);
  motion(Component, { forwardMotionProps: true })

  return (
    <div className="post">
      <div className="container">
        <div className="user">
          <div className="leftSideUserInfo">
            <img src={post.profilePic} alt="" />
            <div className="details">
              <Link
                to={`/profile/${post.userId}`}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                <span className="name">{post.name}</span>
              </Link>
            </div>
          </div>
          <div className="rightSideUserInfo">
            <div className="trustMetric">
              <div className="percentageTrust">
                Trust: {percentageTrust}{percentageTrust > -1 && <span>%</span>}
              </div>
              <div className="totalRating">
                {totalRatings > 0 && <span>({totalRatings})</span>}
              </div>
            </div>
            <MoreHorizIcon style={{ cursor: "pointer" }} onClick={() => setMenuOpen(!menuOpen)} />
            {menuOpen && post.userId === currentUser.id && (
              <button onClick={handleDelete}>delete</button>
            )}
          </div>
        </div>
        <div className="content">
          <p>{post.desc}</p>
          {/*<img src={"./upload/" + post.img} alt="" />*/}
        </div>
        <div className="icons">
          <div className="info">
            <div className="item">
              {likes.isLoading ? (
                "loading"
              ) : likes.data.includes(currentUser.id) ? (
                <FavoriteOutlinedIcon style={{ color: "red" }} onClick={handleLike} />
              ) : (
                <FavoriteBorderOutlinedIcon onClick={handleLike} />
              )}
              {likes.data?.length} Likes
            </div>
            <div className="item" onClick={() => setCommentOpen(!commentOpen)}>
              <TextsmsOutlinedIcon />
              Comments
            </div>
            <div className="item" onClick={handlePositiveTrust}>
              {positiveTrusts.isLoading ? (
                "loading"
              ) : positiveTrusts.data.includes(currentUser.id) ? (
                <div className="trustButton">
                  <MoodIcon style={{ color: "green" }} />
                  <span style={{ color: "green" }}>Trustworthy</span>
                </div>
              ) : (
                <div className="trustButton">
                  <MoodIcon style={{ color: "black" }} />
                  <span style={{ color: "black" }}>Trustworthy</span>
                </div>
              )}
            </div>
            <div className="item" onClick={handleNegativeTrust}>
              {negativeTrusts.isLoading ? (
                "loading"
              ) : negativeTrusts.data.includes(currentUser.id) ? (
                <div className="trustButton">
                  <MoodBadIcon style={{ color: "red" }} />
                  <span style={{ color: "red" }}>Trustworthy</span>
                </div>
              ) : (
                <div className="trustButton">
                  <MoodBadIcon style={{ color: "black" }} />
                  <span style={{ color: "black" }}>Trustworthy</span>
                </div>
              )}
            </div>
          </div>
        </div>
        {commentOpen && <MotionComment
          animate={{ y: 200 }}
          postId={post.id} />}
      </div>
    </div >
  );
};

export default Post;
