import { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/authContext";
import "./login.scss";
import axios from "axios";

const Login = () => {

    const [inputs, setInputs] = useState({
    username:"",
    email:"",
    password:"",
    name:""
  })

  const [err, setErr] = useState(null);

  const navigate = useNavigate();

  /* the use of a spread operator (...prev) means all values are kept the same except the one that is 
  being 'focused' on by the user, which will be changed as the user types
  */
  const handleChange = e =>{
    setInputs(prev=>({...prev, [e.target.name]:e.target.value})) 
  };

  const { login } = useContext(AuthContext);

  const handleLogin = async (e) => {
    e.preventDefault() // prevents refreshing page
    try{
      await login(inputs);
      navigate("/"); // Redirects user to homepage upon succesful login
    } catch (err){
      setErr(err.response.data);
    }
  };

  return (
    <div className="login">
      <div className="card">
        <div className="left">
          <h1>Hello World.</h1>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero cum,
            alias totam numquam ipsa exercitationem dignissimos, error nam,
            consequatur.
          </p>
          <span>Don't you have an account?</span>
          <Link to="/register">
            <button>Register</button>
          </Link>
        </div>
        <div className="right">
          <h1>Login</h1>
          <form>
            <input type="text" placeholder="Username" name="username" onChange={handleChange}/>
            <input type="password" placeholder="Password" name="password" onChange={handleChange}/>
            {err && err} {/* Show the error above login button (if there is one) */}
            <button onClick={handleLogin}>Login</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
