import "./post.scss";
import FavoriteBorderOutlinedIcon from "@mui/icons-material/FavoriteBorderOutlined";
import FavoriteOutlinedIcon from "@mui/icons-material/FavoriteOutlined";
import TextsmsOutlinedIcon from "@mui/icons-material/TextsmsOutlined";
import MoreHorizIcon from "@mui/icons-material/MoreHoriz";
import MoodIcon from '@mui/icons-material/Mood';
import MoodBadIcon from '@mui/icons-material/MoodBad';
import { Link } from "react-router-dom";
import Comments from "../comments/Comments";
import { useState } from "react";
import { useQuery, useQueryClient, useMutation, useQueries } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import { useContext } from "react";
import { AuthContext } from "../../contexts/authContext";

const Post = ({ post }) => {
  const [commentOpen, setCommentOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const {currentUser} = useContext(AuthContext);


  // Handles user likes, positive trusts (pt), 
  const queryConfigurations = [
    {
      queryKey: ["likes", post.id],
      queryFn: () =>
        makeRequest.get("/likes?postId=" + post.id).then((res) => {
          return res.data;
        }),
    },
    {
      queryKey: ["positiveTrusts", post.id],
      queryFn: () =>
        makeRequest.get("/positiveTrusts?postId=" + post.id).then((res) => {
          return res.data;
        }),
    },
  ];

  const queryResults = Promise.all(
    queryConfigurations.map((config) => queryClient.fetchQuery(config.queryKey, config.queryFn))
  );


    // Find the result for the "likes" query
  const likesQuery = queryResults.find(
    (result) => result.queryKey[0] === "likes"
  );

  // Access the loading, error, and data for the "likes" query
  const likesLoading = likesQuery?.isLoading;
  const likesError = likesQuery?.error;
  const likesData = likesQuery?.data;

    // Find the result for the "positiveTrusts" query
  const ptQuery = queryResults.find(
    (result) => result.queryKey[0] === "positiveTrusts"
  );

  // Access the loading, error, and data for the "likes" query
  const ptLoading = ptQuery?.isLoading;
  const ptError = ptQuery?.error;
  const ptData = ptQuery?.data;

  const queryClient = useQueryClient()
  const likesMutation = useMutation(
    (liked) => {
      if (liked) return makeRequest.delete("/likes?postId=" + post.id);
      return makeRequest.post("/likes", { postId: post.id });
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["likes"]);
      },
    }
  );

  const handleLike = () => {
    likesMutation.mutate(likesData.includes(currentUser.id))
  }

  // Handling POSITIVE TRUSTS
  const ptMutation = useMutation(
    (positiveTrusted) => {
      if (positiveTrusted) return makeRequest.delete("/positiveTrusts?postId=" + post.id);
      return makeRequest.post("/positiveTrusts", { postId: post.id });
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["positiveTrusts"]);
      },
    }
  );

  const handlePositiveTrust = () => {
    ptMutation.mutate(ptData.includes(currentUser.id))
  }


  // Deletes post
  const deleteMutation = useMutation(
    (postId) => {
      return makeRequest.delete("/posts/" + postId);
    },
    {
      onSuccess: () => {
        // Invalidate and refetch
        queryClient.invalidateQueries(["posts"]);
      },
    }
  );

  const handleDelete = () => {
    deleteMutation.mutate(post.id);

  }

  console.log(ptData);

  return (
    <div className="post">
      <div className="container">
        <div className="user">
          <div className="userInfo">
            <img src={post.profilePic} alt="" />
            <div className="details">
              <Link
                to={`/profile/${post.userId}`}
                style={{ textDecoration: "none", color: "inherit" }}
              >
                <span className="name">{post.name}</span>
              </Link>
            </div>
          </div>
          <MoreHorizIcon style={{cursor:"pointer"}} onClick={()=>setMenuOpen(!menuOpen)} />
          {menuOpen && post.userId === currentUser.id && (
          <button onClick={handleDelete}>delete</button>
          )}
        </div>
        <div className="content">
          <p>{post.desc}</p>
          <img src={"./upload/" + post.img} alt="" />
        </div>
        <div className="info">
          <div className="item">
            {likesLoading ? (
              "loading"
            ) : likesData?.includes(currentUser.id) ? (
              <FavoriteOutlinedIcon style={{ color: "red" }} onClick={handleLike} />
            ) : (
              <FavoriteBorderOutlinedIcon onClick={handleLike} />
            )}
            {likesData?.length} Likes
          </div>
          <div className="item" onClick={() => setCommentOpen(!commentOpen)}>
            <TextsmsOutlinedIcon />
            Comments
          </div>
          <div className="item">
            {ptLoading ? (
              "loading"
            ) : ptData?.includes(currentUser.id) ? (
              <MoodIcon style={{ color: "green" }} onClick={handlePositiveTrust} />
            ) : (
              <MoodIcon style={{ color: "black" }} onClick={handlePositiveTrust} />
            )}
            Trustworthy
          </div>
          <div className="item">
            <MoodBadIcon />
            Untrustworthy
          </div>
        </div>
        {commentOpen && <Comments postId={post.id}/>}
      </div>
    </div>
  );
};

export default Post;
