import "./navbar.scss"
import HomeIcon from '@mui/icons-material/Home';
import ChatBubbleOutlineIcon from '@mui/icons-material/ChatBubbleOutline';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import SearchOutlinedIcon from "@mui/icons-material/SearchOutlined";
import { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { AuthContext } from "../../contexts/authContext";
import axios from "axios";


function Navbar() {
  const { currentUser } = useContext(AuthContext);
  const [search, setSearch] = useState(null);
  const [searchAllUsers, setSearchAllUsers] = useState([]);


  const handleChange = e => {
    setSearch(e.target.value)
  };

  const handleClick = async (inputs) => {
    const res = await axios.get("http://localhost:3001/api/users/find/allUsers", {
      withCredentials: true, // needed for working with cookies
    });
    setSearchAllUsers(res.data);
  };



  return (
    <div className="navbar">
      <div className="left">
        <h1>trustSocial</h1>
      </div>
      <div className="middle">
        <div className="icons">
          <Link to="/">
            <div>
              <motion.div whileHover={{ scale: 1.2 }} whileTap={{ scale: 0.8 }} />
              <HomeIcon
                className="button"
                fontSize="large"
              />
            </div>
          </Link>
          <ChatBubbleOutlineIcon
            className="button"
            fontSize="large"
          />
          <Link to={`profile/${currentUser.id}`}>
            <AccountCircleIcon
              className="button"
              fontSize="large"
            />
          </Link>
        </div>
      </div>
      <div className="right">
        <div className="search">
          <SearchOutlinedIcon className="searchButton" onClick={handleClick} />
          <input type="text"
            placeholder="Search trustMe..."
            name="search"
            onChange={handleChange}
          />
        </div>
        <div className="searchResults">
          {
            Object.keys(searchAllUsers).map((item, i) => (
              <div key={i}>
                {search.includes(searchAllUsers[item].name) && <span>{<img src={searchAllUsers[item].profilePic} />}{searchAllUsers[item].name}</span>}
              </div>
            ))
          }
        </div>
      </div>
    </div>
  )
}

export default Navbar
