import { useState } from "react";
import "./update.scss";
import { makeRequest } from "../../axios";
import { useMutation, useQueryClient } from "@tanstack/react-query";


const Update = ({ setOpenUpdate, user }) => {


  const [profile, setProfile] = useState(null)
  const [texts, setTexts] = useState({
    name: "",
    city: "",
    website: "",
  });

  const handleChange = (e) => {
    setTexts((prev) => ({ ...prev, [e.target.name]: [e.target.value] }))
  };

  const queryClient = useQueryClient();

  const mutation = useMutation(
    (user) => {
      return makeRequest.put("/users", user);
    },
    {
      onSuccess: () => {
        queryClient.invalidateQueries(["user"])
      },
    }
  );

  const handleClick = async e => {
    e.preventDefault();
    let profileUrl; // Preserves users current profile pic

    // if we have a file we will update the users profile with it with the new url from upload, otherwise we will use the previous pic
    profileUrl = profile ? await upload(profile) : user.profilePic


    mutation.mutate({ ...texts, coverPic: coverUrl, profilePic: profileUrl });
    setOpenUpdate(false);
  };

  const upload = async (file) => {
    try {
      const formData = new FormData(); // we cannot send file directly to api so we must use formdata
      formData.append("file", file)
      const res = await makeRequest.post("/upload", formData);
      return res.data
    } catch (err) {
      console.log(err);
    }
  }



  return (
    <div className="profile">
      {isLoading ? "loading" : <> <div className="images">
        <img
          src={"/upload/" + data.profilePic}
          alt=""
          className="profilePic"
        />
      </div>
        <div className="profileContainer">
          <div className="uInfo">
            <div className="center">
              <span>{data.name}</span>
              <div className="info">
                <div className="item">
                  <PlaceIcon />
                  <span>{data.city}</span>
                </div>
              </div>
              {rIsLoading ? (
                "loading"
              ) : user.Id === currentUser.id ? (
                <button onClick={() => setOpenUpdate(true)}>Update</button>
              ) : (
                // Below shows that if the user profile page being viewed is already being followed, they will already exist in the mysql relationship table
                // and thus the button will show 'Following', if not the button will show '
                <button onClick={handleFollow}>
                  {relationshipData.includes(currentUser.id)
                    ? "Following"
                    : "Follow"}
                </button>
              )}
            </div>
          </div>
          <Posts userId={userId} />
        </div></>
      }
      {openUpdate && <Update setOpenUpdate={setOpenUpdate} user={data} />}
    </div>
  );
};

export default Profile;
