import { Link } from "react-router-dom";
import "./register.scss";
import { useState } from "react";
import  axios  from "axios";

const Register = () => {

  const [inputs, setInputs] = useState({
    username:"",
    email:"",
    password:"",
    name:""
  })

  const [err, setErr] = useState(null);

  /* the use of a spread operator (...prev) means all values are kept the same except the one that is 
  being 'focused' on by the user, which will be changed as the user types
  */
  const handleChange = e =>{
    setInputs(prev=>({...prev, [e.target.name]:e.target.value})) 
  };

  const handleClick = async e=>{
    e.preventDefault() // stops page from refreshing when button is clicked

    try {
      await axios.post("http://localhost:8800/api/auth/register", inputs)
    } catch(err){
      setErr(err.response.data);
    }
  };

  console.log(err);

  return (
    <div className="register">
      <div className="card">
        <div className="left">
          <h1>Tharan Social.</h1>
          <p>
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero cum,
            alias totam numquam ipsa exercitationem dignissimos, error nam,
            consequatur.
          </p>
          <span>Do you have an account?</span>
          <Link to="/login">
          <button>Login</button>
          </Link>
        </div>
        <div className="right">
          <h1>Register</h1>
          <form>
            <input type="text" placeholder="Username" name="username" onChange={handleChange}/> {/* 'name' must match exactly what is int he useState hook */}
            <input type="email" placeholder="Email" name="email" onChange={handleChange}/>
            <input type="password" placeholder="Password" name="password" onChange={handleChange}/>
            <input type="text" placeholder="Name" name="name" onChange={handleChange}/>
            {err && err}
            <button onClick={handleClick}>Register</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
