import "./profile.scss";
import PlaceIcon from "@mui/icons-material/Place";
import LanguageIcon from "@mui/icons-material/Language";
import Posts from "../../components/posts/Posts"
import { useQuery, useQueryClient, useMutation } from "@tanstack/react-query";
import { makeRequest } from "../../axios";
import { useLocation } from "react-router-dom";
import { useContext, useState } from "react";
import { AuthContext } from "../../contexts/authContext";

const Profile = () => {

  const [openUpdate, setOpenUpdate] = useState(false);
  const { currentUser } = useContext(AuthContext);

  const userId = parseInt(useLocation().pathname.split("/")[2]); // as userId is string but currentUser.id is an int

  const { isLoading, error, data } = useQuery(
    ["user"],
    () =>
      makeRequest.get("/users/find/" + userId).then((res) => {
        return res.data;
      })
  );

  const { isLoading: rIsLoading, data: relationshipData } = useQuery(
    ["relationship"],
    () =>
      makeRequest.get("/relationships?followedUserId=" + userId).then((res) => {
        return res.data;
      })
  );

  const queryClient = useQueryClient();

  const mutation = useMutation(
    (following) => {
      if (following)
        return makeRequest.delete("/relationships?userId=" + userId);
      return makeRequest.post("/relationships", { userId });
    },
    {
      onSuccess: () => {
        // Invalidate and refetches user to page autorefreshes and shows new data
        queryClient.invalidateQueries(["relationship"]);
      },
    }
  );

  const handleFollow = () => {
    mutation.mutate(relationshipData.includes(currentUser.id))
  }


  return (
    <div className="profile">
      {isLoading ? "loading" : <> <div className="images">
        <img
          src={data.profilePic}
          alt=""
          className="profilePic"
        />
      </div>
        <div className="profileContainer">
          <div className="uInfo">
            <div className="left">
            </div>
            <div className="center">
              <span>{data.name}</span>
              <div className="info">
                <div className="item">
                  <PlaceIcon />
                  <span>{data.city}</span>
                </div>
                <div className="item">
                  <LanguageIcon />
                  <span>{data.website}</span>
                </div>
              </div>
              {rIsLoading ? (
                "loading"
              ) : userId === currentUser.id ? (
                <button onClick={() => setOpenUpdate(true)}>Update</button>
              ) : (
                // Below shows that if the user profile page being viewed is already being followed, they will already exist in the mysql relationship table
                // and thus the button will show 'Following', if not the button will show '
                <button onClick={handleFollow}>
                  {relationshipData.includes(currentUser.id)
                    ? "Following"
                    : "Follow"}
                </button>
              )}
            </div>
            <div className="right">
            </div>
          </div>
          <Posts userId={userId} />
        </div></>
      }
      {/*openUpdate && <Update setOpenUpdate={setOpenUpdate} user={data} />*/}
    </div>
  );
};

export default Profile;
